mapper.select:
SELECT COUNT(*) as cnt
FROM dept

	 
		WHERE univno = #{univno}
	
	
		WHERE univno= #{univno}
		and dept_name LIKE '%' || #{word} || '%'

		SELECT deptno, univno, dept_type, dept_name, r
		FROM (
		SELECT deptno, univno, dept_type, dept_name, rownum as r
		FROM dept
		
			
				WHERE univno = #{univno}
			
			
			    WHERE univno = #{univno}
				and	dept_name LIKE '%' || #{word} || '%'
			
			

		
		ORDER BY deptno

		)
		WHERE  r >= #{start_num} AND r <= #{end_num}
